function [nparam,models]=nparammodels;% nparammodels              - name and # of parameters for covariance models (Jan 1, 2001)%% Return a cell array that contains the name of the available% covariance model and the number of parameters associated with% each one of these models.%% This function is used by coord2K in the space-time case when% the space-time covariance model is separable.%% SYNTAX :%% [nparam,models]=nparammodels;%% OUTPUT :%% models    k by 1  cell array where each cells is a string that contains%                   the name of an available covariance model.% nparam    k by 1  cell array that contains the number of parameters for%                   the corresponding covariance models.models={  'exponentialC'  'gaussianC'  'holecosC'  'holesinC'  'nuggetC'  'sphericalC'};nparam={   2   2   2   2   1   2};